import React from "react";
import { useNavigate } from "react-router-dom";

const courses = [
  {
    id: 1,
    title: "JavaScript for Beginners",
    description: "Master JavaScript with hands-on projects and real-world examples.",
    image: "https://via.placeholder.com/300x200",
  },
  {
    id: 2,
    title: "React Essentials",
    description: "Learn React and build dynamic, interactive web applications.",
    image: "https://via.placeholder.com/300x200",
  },
  {
    id: 3,
    title: "Python Programming",
    description: "A comprehensive guide to Python for all levels.",
    image: "https://via.placeholder.com/300x200",
  },
  {
    id: 4,
    title: "Data Structures and Algorithms",
    description: "Optimize your code with advanced data structures and algorithms.",
    image: "https://via.placeholder.com/300x200",
  },
];

const Courses = () => {
  const navigate = useNavigate();

  const handleEnroll = (courseId) => {
    alert(`You have enrolled in course ID: ${courseId}`);
    navigate(`/courses/${courseId}`);
  };

  return (
    <div className="courses-container">
      <h1 className="courses-title">Explore Our Courses</h1>
      <div className="courses-grid">
        {courses.map((course) => (
          <div className="course-card" key={course.id}>
            <img
              src={course.image}
              alt={course.title}
              className="course-image"
            />
            <div className="course-content">
              <h3 className="course-title">{course.title}</h3>
              <p className="course-description">{course.description}</p>
              <button
                className="enroll-button"
                onClick={() => handleEnroll(course.id)}
              >
                Enroll Now
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Courses;
