import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/global.css'; // Assuming global CSS is imported here

const Header = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false); // State to track login status
  const [userName, setUserName] = useState(''); // State to store the user's name

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const handleLogin = () => {
    // Simulate logging in (In reality, this would be handled via authentication API)
    setIsLoggedIn(true);
    setUserName('John Doe'); // Set the logged-in user's name
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUserName(''); // Clear the user's name
  };

  return (
    <header>
      <div className="logo">
        <a href="/" className="logo-link">AICADEMY</a>
      </div>
      <nav className={menuOpen ? 'open' : ''}>
        <Link to="/">Home</Link>
        <Link to="/courses">Courses</Link>
        <Link to="/dashboard">Dashboard</Link>

        {/* Conditionally render based on login status */}
        {isLoggedIn ? (
          <>
            <span className="user-name">{userName}</span>
            <button onClick={handleLogout}>Logout</button>
          </>
        ) : (
          <>
            <Link to="/login" onClick={handleLogin}>Login</Link>
            <Link to="/register">Register</Link>
          </>
        )}
      </nav>
      <div className="hamburger-menu" onClick={toggleMenu}>
        <div></div>
        <div></div>
        <div></div>
      </div>
    </header>
  );
};

export default Header;
